INSERT INTO movies(id,slug,name,description,genres,rate,length,img) VALUES (1,'deadpool','Deadpool','A former Special Forces operative turned mercenary is subjected to a rogue experiment that leaves him with accelrated healing powers, adopting the alter ego Deadpool.','action,adventure,comedy',8.6,'1hr 48mins','deadpool.jpg');
INSERT INTO movies(id,slug,name,description,genres,rate,length,img) VALUES (2,'we-are-the-millers','We''re the Millers','A veteran pot dealer creates a fake family as part of his plan to move a huge shipment of weed into the U.S. from Mexico.','adventure,comedy,crime',7.0,'1hr 50mins','we-are-the-millers.jpg');
INSERT INTO movies(id,slug,name,description,genres,rate,length,img) VALUES (3,'straight-outta-compton','Straight Outta Compton','The group NWA emerges from the mean streets of Compton in Los Angeles, California, in the mid-1980s and revolutionizes Hip Hop culture with their music and tales about life in the hood.','biography,drama,history',8.0,'2hr 27mins','straight-outta-compton.jpg');
