INSERT INTO movies(id,slug,name,description,genres,rate,length,img) VALUES (24,'bad-boys','Bad Boys','Two hip detectives protect a murder witness while investigating a case of stolen heroin.','action,comedy,crime',6.8,'1hr 59mins','bad-boys.jpg');
